package tw.com.code.demo;

public class Demo02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 100;
		int b = 200;
		//Byte int 只要進行四則運算
		int t1 =a+b;//加
		int t2 = a-b;//減
		int t3 = a*b;//乘
		int t4 = a/b;//除
		int t5 = a%b;//取餘數
		
		System.out.println(t1);
		System.out.println(t2);
		System.out.println(t3);
		System.out.println(t4);
		System.out.println(t5);
	}

}
