package tw.com.code.demo;

public class Demo08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 0 ;
		while(a<10)
		{
			System.out.println(a);
			a++;
		}
		System.out.println("--------------------");
		for(int i=0;i<10;i++)
		{
			System.out.println(i);
		}
	}

}
